import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import SupervisorSpiderChart from "./SupervisorSpider";
import SpiderChart from "./SpiderChart";
import Guidelines from "../Guidelines/Guidelines";
import { Link } from "react-router-dom";
import LOGO from "../../assets/nock j.png";
import ScoreFormManager from "../ScoreInput/ScoreFormManager";
import "./Modern.css";

const categories = [
  "Geology",
  "Geophysics",
  "Geochemistry",
  "Petroleum Engineering",
  "Project Management",
  "Renewable Energy",
  "Field Work",
  "Programming",
];

export default function ModernSpiderChart() {
  const [showPopup, setShowPopup] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [dbScores, setDbScores] = useState(null);
  const [user, setUser] = useState(null);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState("");
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState("employee"); // 'employee' or 'supervisor'
  const dropdownRef = useRef(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await axios.get("http://localhost:8000/api/auth/me", {
          withCredentials: true,
        });
        const userData = res.data.user;
        setUser(userData);

        if (userData.submissions?.length > 0) {
          const submission = userData.submissions[0];
          setDbScores({
            currentScores: Object.fromEntries(
              categories.map((cat) => [cat, submission.currentScore[cat] || 0])
            ),
            projectedScores: Object.fromEntries(
              categories.map((cat) => [cat, submission.projected[cat] || 0])
            ),
            supervisorScores: submission.supervisorAssessment
              ? Object.fromEntries(
                  categories.map((cat) => [
                    cat,
                    submission.supervisorAssessment[cat] || 0,
                  ])
                )
              : null,
            status: submission.status || "Pending",
            comments: submission.approvalComments || "",
          });
          if (submission.supervisorAssessment) {
            setNotificationMessage(
              'ðŸŽ‰ Your supervisor has reviewed your assessment! Click "View Assessment" to see the results.'
            );
            setShowNotification(true);
          }
          setIsLoading(false);
        } else {
          setIsLoading(false);
        }
      } catch (err) {
        setError(err.response?.data?.message || "Failed to load user data.");
        setIsLoading(false);
        if (err.response?.status === 401) {
          window.location.href = "/login";
        }
      }
    };

    fetchUserData();

    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleDropdown = () => setIsDropdownOpen((prev) => !prev);

  const handleSubmission = () => {
    setIsLoading(true);
    setShowNotification(true);
    setNotificationMessage("âœ… Assessment submitted successfully!");
    setTimeout(() => window.location.reload(), 1500);
  };

  const toggleViewMode = () => {
    setViewMode((prev) => (prev === "employee" ? "supervisor" : "employee"));
    setNotificationMessage((prev) =>
      prev === "employee"
        ? "ðŸ“Š Viewing your supervisor's assessment."
        : "ðŸ“ˆ Viewing your submitted scores."
    );
  };

  const handleLogout = async () => {
    try {
      await axios.post(
        "http://localhost:8000/api/auth/logout",
        {},
        { withCredentials: true }
      );
      window.location.href = "/login";
    } catch (err) {
      console.error("Logout failed:", err);
    }
  };

  if (error) return <div className="error-message">{error}</div>;

  if (isLoading || !user) {
    return (
      <div className="chart-loading-container">
        <div className="chart-loading-spinner">
          <div className="spinner-circle"></div>
        </div>
        <div className="chart-loading-content">
          <img src={LOGO} alt="NOCK Logo" className="loading-logo" />
          <h3 className="chart-loading-text">Loading Your Data...</h3>
          <p className="chart-loading-subtext">Please wait a moment</p>
        </div>
      </div>
    );
  }

  const initials = user.name
    .split(" ")
    .map((n) => n[0])
    .slice(0, 2)
    .join("");

  return (
    <div className="modern-chart-container">
      <header className="modern-header">
        <div className="modern-logo">
          <div className="logo-placeholder">
            <img className="logo-icon" src={LOGO} alt="logo" />
            <span className="logo-text">Energizing Kenya</span>
          </div>
        </div>
        <div className="modern-profile" ref={dropdownRef}>
          <div className="profile-container" onClick={toggleDropdown}>
            <div className="profile-avatar">{initials}</div>
            <div className="profile-info">
              <h3>{user.name}</h3>
              <p>{user.occupation}</p>
            </div>
            <div className={`dropdown-arrow ${isDropdownOpen ? "open" : ""}`}>
              â–¼
            </div>
          </div>
          <div className={`dropdown-menu ${isDropdownOpen ? "open" : ""}`}>
            <Link to="/" className="dropdown-item">
              View Assessment
            </Link>
            <div className="dropdown-divider"></div>
            <Link
              to="/logout"
              onClick={handleLogout}
              className="dropdown-item logout"
            >
              Log Out
            </Link>
          </div>
        </div>
      </header>
      <div className="modern-title-container">
        <h2 className="modern-title">Upstream Competency Mapping</h2>
        <div className="title-decoration"></div>
      </div>
      <div className="modern-guidelines-button">
        <button
          onClick={() => setShowPopup(true)}
          className="modern-button guidelines-btn"
        >
          Open Competency Guidelines <span className="button-icon">ðŸ“Š</span>
        </button>
        {showPopup && <Guidelines onClose={() => setShowPopup(false)} />}
      </div>
      <div className="content-container">
        <div className="content">
          {showNotification && (
            <div
              className={`submission-notification ${
                !showNotification ? "hide" : ""
              }`}
            >
              <div className="notification-content">
                <p>{notificationMessage}</p>
                <button
                  className="close-notification"
                  onClick={() => setShowNotification(false)}
                >
                  Ã—
                </button>
              </div>
            </div>
          )}
          {isLoading ? (
            <div className="chart-loading-container">
              <div className="chart-loading-spinner">
                <div className="spinner-circle"></div>
              </div>
              <div className="chart-loading-content">
                <h3 className="chart-loading-text">Generating Your Chart...</h3>
                <p className="chart-loading-subtext">Please wait a moment</p>
              </div>
            </div>
          ) : dbScores ? (
            <div className="chart-wrapper">
              <div className="view-toggle">
                <button
                  className={`modern-button toggle-btn ${
                    viewMode === "employee" ? "active" : ""
                  }`}
                  onClick={toggleViewMode}
                >
                  My Scores
                </button>
                <button
                  className={`modern-button toggle-btn ${
                    viewMode === "supervisor" ? "active" : ""
                  }`}
                  onClick={toggleViewMode}
                  disabled={!dbScores.supervisorScores}
                >
                  On My Way! <span className="button-icon">ðŸš€</span>
                </button>
              </div>
              {viewMode === "supervisor" && dbScores.supervisorScores ? (
                <SupervisorSpiderChart
                  categories={categories}
                  currentScores={dbScores.currentScores}
                  projectedScores={dbScores.projectedScores}
                  supervisorScores={dbScores.supervisorScores}
                  status={dbScores.status}
                  approvalComments={dbScores.comments}
                  userName={user.name}
                />
              ) : viewMode === "supervisor" ? (
                <p className="no-review-message">
                  Your assessment is {dbScores.status.toLowerCase()}. No
                  supervisor review available yet.
                </p>
              ) : (
                <SpiderChart
                  currentScores={dbScores.currentScores}
                  projectedScores={dbScores.projectedScores}
                  categories={categories}
                />
              )}
            </div>
          ) : (
            <div>
              <p className="no-submission-message">
                Please complete the assessment form below.
              </p>
              <ScoreFormManager
                categories={categories}
                onScoresChange={handleSubmission}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
