import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import SpiderChart from "./SpiderChart";
import Guidelines from "../Guidelines/Guidelines";
import { Link } from "react-router-dom";
import LOGO from "../../assets/nock j.png";
import ScoreFormManager from "../ScoreInput/ScoreFormManager";
import "./Modern.css";

const categories = [
  "Geology",
  "Geophysics",
  "Geochemistry",
  "Petroleum Engineering",
  "Project Management",
  "Renewable Energy",
  "Field Work",
  "Reporting",
];

export default function ModernSpiderChart() {
  const [showPopup, setShowPopup] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isLoadingChart, setIsLoadingChart] = useState(false);
  const [dbScores, setDbScores] = useState(null);
  const [user, setUser] = useState(null);
  const [hasSubmitted, setHasSubmitted] = useState(false);
  const [showSubmissionMessage, setShowSubmissionMessage] = useState(false);
  const [submissionMessage, setSubmissionMessage] = useState("");
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState("employee"); // "employee" or "supervisor"
  const dropdownRef = useRef(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await axios.get("http://localhost:4000/api/auth/me", {
          withCredentials: true,
        });
        const userData = res.data.user;
        console.log("Fetched user data:", JSON.stringify(userData, null, 2));
        console.log("User submissions:", userData.submissions);
        setUser(userData);

        const hasScores =
          userData.submissions &&
          userData.submissions.length > 0 &&
          userData.submissions[0].current &&
          userData.submissions[0].projected;

        console.log("Has scores:", hasScores);

        if (hasScores) {
          const dbCurrentScores = Object.fromEntries(
            categories.map((cat) => [
              cat,
              userData.submissions[0].current[cat] || 0,
            ])
          );
          const dbProjectedScores = Object.fromEntries(
            categories.map((cat) => [
              cat,
              userData.submissions[0].projected[cat] || 0,
            ])
          );
          const dbSupervisorScores = userData.submissions[0]
            .supervisorAssessment
            ? Object.fromEntries(
                categories.map((cat) => [
                  cat,
                  userData.submissions[0].supervisorAssessment[cat] || 0,
                ])
              )
            : null;

          console.log("Database current scores:", dbCurrentScores);
          console.log("Database projected scores:", dbProjectedScores);
          console.log("Database supervisor scores:", dbSupervisorScores);

          setDbScores({
            currentScores: dbCurrentScores,
            projectedScores: dbProjectedScores,
            supervisorScores: dbSupervisorScores,
            status: userData.submissions[0].status,
            approvalComments: userData.submissions[0].approvalComments,
          });
          setHasSubmitted(true);
          setShowSubmissionMessage(true);
          setSubmissionMessage(
            "This Spider Chart displays your submitted competency scores."
          );
        } else {
          console.log("No submissions found; showing form.");
        }
      } catch (err) {
        console.error(
          "Error fetching user:",
          err.response?.data || err.message
        );
        setError(err.response?.data?.message || "Failed to load user data.");
        if (err.response?.status === 401) {
          window.location.href = "/login";
        }
      }
    };
    fetchUserData();

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleSubmission = () => {
    setIsLoadingChart(true);
    setShowSubmissionMessage(true);
    setSubmissionMessage(
      "âœ… Scores submitted successfully! Loading your chart..."
    );

    setTimeout(() => {
      window.location.reload();
    }, 1500);
  };

  const toggleViewMode = () => {
    setViewMode(viewMode === "employee" ? "supervisor" : "employee");
    setSubmissionMessage(
      viewMode === "employee"
        ? "This Spider Chart displays your supervisor's reviewed assessment."
        : "This Spider Chart displays your submitted competency scores."
    );
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!user) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">
          <div className="spinner-sector spinner-sector-primary"></div>
          <div className="spinner-sector spinner-sector-secondary"></div>
          <div className="spinner-sector spinner-sector-tertiary"></div>
        </div>
        <div className="loading-content">
          <img src={LOGO} alt="NOCK Logo" className="loading-logo" />
          <h2 className="loading-text">Loading Your Data...</h2>
          <p className="loading-subtext">Please wait while we prepare</p>
        </div>
      </div>
    );
  }

  const nameParts = user.name.split(" ");
  const initials =
    nameParts.length >= 2
      ? `${nameParts[0][0]}${nameParts[nameParts.length - 1][0]}`
      : nameParts[0][0];

  return (
    <div className="modern-chart-container">
      <header className="modern-header">
        <div className="modern-logo">
          <div className="logo-placeholder">
            <img className="logo-icon" src={LOGO} alt="logo" />
            <span className="logo-text">Energizing Kenya</span>
          </div>
        </div>
        <div className="modern-profile" ref={dropdownRef}>
          <div className="profile-container" onClick={toggleDropdown}>
            <div className="profile-avatar">{initials}</div>
            <div className="profile-info">
              <h3>{user.name}</h3>
              <p>{user.occupation}</p>
            </div>
            <div className={`dropdown-arrow ${isDropdownOpen ? "open" : ""}`}>
              â–¼
            </div>
          </div>
          <div className={`dropdown-menu ${isDropdownOpen ? "open" : ""}`}>
            <Link to="/" className="dropdown-item">
              View Assessment
            </Link>
            <div className="dropdown-divider"></div>
            <Link to="/logout" className="dropdown-item logout">
              Log Out
            </Link>
          </div>
        </div>
      </header>
      <div className="modern-title-container">
        <h2 className="modern-title">Upstream Competency Mapping</h2>
        <div className="title-decoration"></div>
      </div>
      <div className="modern-guidelines-button">
        <button
          onClick={() => setShowPopup(true)}
          className="modern-button guidelines-btn"
        >
          Open Competency Guidelines <span className="button-icon">ðŸ“Š</span>
        </button>
        {showPopup && <Guidelines onClose={() => setShowPopup(false)} />}
      </div>
      <div className="content-container">
        <div className="content">
          {showSubmissionMessage && (
            <div
              className={`submission-notification ${
                !showSubmissionMessage ? "hide" : ""
              }`}
            >
              <div className="notification-content">
                <p>{submissionMessage}</p>
                <button
                  className="close-notification"
                  onClick={() => setShowSubmissionMessage(false)}
                >
                  Ã—
                </button>
              </div>
            </div>
          )}
          {isLoadingChart ? (
            <div className="chart-loading">
              <div className="loading-spinner">
                <div className="spinner-sector spinner-sector-primary"></div>
                <div className="spinner-sector spinner-sector-secondary"></div>
                <div className="spinner-sector spinner-sector-tertiary"></div>
              </div>
              <p>Loading your competency chart...</p>
            </div>
          ) : hasSubmitted && dbScores ? (
            <div className="chart-wrapper">
              <div className="view-toggle">
                <button
                  className={`modern-button toggle-btn ${
                    viewMode === "employee" ? "active" : ""
                  }`}
                  onClick={toggleViewMode}
                >
                  My Scores
                </button>
                <button
                  className={`modern-button toggle-btn ${
                    viewMode === "supervisor" ? "active" : ""
                  }`}
                  onClick={toggleViewMode}
                >
                  Supervisor Review
                </button>
              </div>
              {viewMode === "supervisor" && !dbScores.supervisorScores ? (
                <p className="no-review-message">
                  Your assessment is {dbScores.status.toLowerCase()}. No
                  supervisor review is available yet.
                </p>
              ) : (
                <SpiderChart
                  currentScores={
                    viewMode === "supervisor" && dbScores.supervisorScores
                      ? dbScores.supervisorScores
                      : dbScores.currentScores
                  }
                  projectedScores={
                    viewMode === "employee" ? dbScores.projectedScores : null
                  }
                  categories={categories}
                />
              )}
              {viewMode === "supervisor" && (
                <div className="review-details">
                  <p className="review-status">
                    Status:{" "}
                    <span className={`status-${dbScores.status.toLowerCase()}`}>
                      {dbScores.status}
                    </span>
                  </p>
                  {dbScores.approvalComments && (
                    <p className="review-comments">
                      Comments: {dbScores.approvalComments}
                    </p>
                  )}
                </div>
              )}
            </div>
          ) : (
            <div>
              <p className="no-submission-message">
                You have not submitted any scores yet. Please complete the form
                below.
              </p>
              <ScoreFormManager
                categories={categories}
                onScoresChange={handleSubmission}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
