import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import SpiderChart from "./SpiderChart";
import Guidelines from "../Guidelines/Guidelines";
import { Link } from "react-router-dom";
import LOGO from "../../assets/nock j.png";
import ScoreFormManager from "../ScoreInput/ScoreFormManager";
import "./Modern.css";

const categories = [
  "Geology",
  "Geophysics",
  "Geochemistry",
  "Petroleum Engineering",
  "Project Management",
  "Renewable Energy",
  "Field Work",
  "Reporting",
];

export default function ModernSpiderChart() {
  const [showPopup, setShowPopup] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [scores, setScores] = useState({
    currentScores: Object.fromEntries(categories.map((cat) => [cat, 0])),
    projectedScores: Object.fromEntries(categories.map((cat) => [cat, 0])),
  });
  const [user, setUser] = useState(null);
  const [hasSubmitted, setHasSubmitted] = useState(false);
  const [submissionStatus, setSubmissionStatus] = useState(null);
  const [reviewFeedback, setReviewFeedback] = useState("");
  const [error, setError] = useState(null);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await axios.get("http://localhost:4000/api/auth/me", {
          withCredentials: true,
        });
        const userData = res.data.user;
        setUser(userData);

        // Fetch latest submission
        const submissionRes = await axios.get(
          "http://localhost:4000/api/scores/pending",
          {
            withCredentials: true,
          }
        );
        const latestSubmission = submissionRes.data[0];
        if (latestSubmission) {
          setSubmissionStatus(latestSubmission.status);
          setReviewFeedback(latestSubmission.reviewFeedback || "");
          if (latestSubmission.status === "approved") {
            setScores({
              currentScores: latestSubmission.currentScores,
              projectedScores: latestSubmission.projectedScores,
            });
            setHasSubmitted(true);
          }
        }
      } catch (err) {
        console.error(
          "Error fetching user:",
          err.response?.data || err.message
        );
        setError(err.response?.data?.message || "Failed to load user data");
        if (err.response?.status === 401) {
          window.location.href = "/login";
        }
      }
    };
    fetchUserData();

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const toggleForm = () => {
    console.log("Toggling form, showForm:", !showForm);
    setShowForm(!showForm);
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!user) {
    return "Loading user...";
  }

  const nameParts = user.name.split(" ");
  const initials =
    nameParts.length >= 2
      ? `${nameParts[0][0]}${nameParts[nameParts.length - 1][0]}`
      : nameParts[0][0];

  return (
    <div className="modern-chart-container">
      <header className="modern-header">
        <div className="modern-logo">
          <div className="logo-placeholder">
            <img className="logo-icon" src={LOGO} alt="logo" />
            <span className="logo-text">Energizing Kenya</span>
          </div>
        </div>
        <div className="modern-profile" ref={dropdownRef}>
          <div className="profile-container" onClick={toggleDropdown}>
            <div className="profile-avatar">{initials}</div>
            <div className="profile-info">
              <h3>{user.name}</h3>
              <p>{user.occupation}</p>
            </div>
            <div className={`dropdown-arrow ${isDropdownOpen ? "open" : ""}`}>
              â–¼
            </div>
          </div>
          <div className={`dropdown-menu ${isDropdownOpen ? "open" : ""}`}>
            <Link to="/" className="dropdown-item">
              View Assessment
            </Link>
            <div className="dropdown-divider"></div>
            <Link to="/logout" className="dropdown-item logout">
              Log Out
            </Link>
          </div>
        </div>
      </header>
      <div className="modern-title-container">
        <h2 className="modern-title">Upstream Competency Mapping</h2>
        <div className="title-decoration"></div>
      </div>
      <div className="modern-guidelines-button">
        <button
          onClick={() => setShowPopup(true)}
          className="modern-button guidelines-btn"
        >
          Open Competency Guidelines <span className="button-icon">ðŸ“Š</span>
        </button>
        {showPopup && <Guidelines onClose={() => setShowPopup(false)} />}
      </div>
      <div className="content-container">
        <div className="content">
          {submissionStatus && (
            <div className="submission-message">
              <p>
                Your submission is <strong>{submissionStatus}</strong>.
                {submissionStatus !== "pending" && reviewFeedback && (
                  <span> Feedback: {reviewFeedback}</span>
                )}
              </p>
            </div>
          )}
          {hasSubmitted && !showForm && (
            <div className="submission-message">
              <p>
                This Spider Chart displays your previously submitted competency
                scores. You can review or update your scores by clicking the
                edit button.
              </p>
            </div>
          )}
          {showForm ? (
            <div className="form-wrapper">
              <ScoreFormManager
                categories={categories}
                onScoresChange={setScores}
                initialScores={scores}
              />
            </div>
          ) : (
            <div className="chart-wrapper">
              <SpiderChart
                currentScores={scores.currentScores}
                projectedScores={scores.projectedScores}
                categories={categories}
              />
            </div>
          )}
        </div>
      </div>
      <button
        className="floating-action-button"
        onClick={toggleForm}
        title={showForm ? "Hide Form" : "Edit Scores"}
      >
        {showForm ? "âœ•" : "âœŽ"}
      </button>
    </div>
  );
}
