// src/components/ModernSpiderChart.js
import React, { useState, useRef, useEffect } from "react";
import SpiderChart from "./SpiderChart";
import Guidelines from "../Guidelines/Guidelines";
import { Link } from "react-router-dom";
import LOGO from "../../assets/nock j.png";
import { getUserFromToken } from "../../utils/auth";
import ScoreFormManager from "../ScoreInput/ScoreFormManager"; // Import new component
import "./Modern.css";

const categories = [
  "Geology",
  "Geophysics",
  "Geochemistry",
  "Petroleum Engineering",
  "Project Management",
  "Renewable Energy",
  "Field Work",
  "Reporting",
];

export default function ModernSpiderChart() {
  const [showPopup, setShowPopup] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [scores, setScores] = useState({
    currentScores: Object.fromEntries(categories.map((cat) => [cat, 0])),
    projectedScores: Object.fromEntries(categories.map((cat) => [cat, 0])),
  });
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setIsDropdownOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const user = getUserFromToken();

  if (!user) {
    return "Please log in";
  }

  // Extract first letters of first and last names
  const nameParts = user.name.split(" ");
  const initials =
    nameParts.length >= 2
      ? `${nameParts[0][0]}${nameParts[nameParts.length - 1][0]}`
      : nameParts[0][0];

  return (
    <div className="modern-chart-container">
      {/* Logo and Header */}
      <header className="modern-header">
        <div className="modern-logo">
          <div className="logo-placeholder">
            <img className="logo-icon" src={LOGO} alt="logo" />
            <span className="logo-text">Energizing Kenya</span>
          </div>
        </div>
        <div className="modern-profile" ref={dropdownRef}>
          <div className="profile-container" onClick={toggleDropdown}>
            <div className="profile-avatar">{initials}</div>
            <div className="profile-info">
              <h3>{user.name}</h3>
              <p>{user.occupation}</p>
            </div>
            <div className={`dropdown-arrow ${isDropdownOpen ? "open" : ""}`}>
              â–¼
            </div>
          </div>
          <div className={`dropdown-menu ${isDropdownOpen ? "open" : ""}`}>
            <Link to="/" className="dropdown-item">
              View Assessment
            </Link>
            <div className="dropdown-divider"></div>
            <Link to="/logout" className="dropdown-item logout">
              Log Out
            </Link>
          </div>
        </div>
      </header>
      <div className="modern-title-container">
        <h2 className="modern-title">Upstream Competency Mapping</h2>
        <div className="title-decoration"></div>
      </div>
      {/* Guidelines Popup */}
      <div className="modern-guidelines-button">
        <button
          onClick={() => setShowPopup(true)}
          className="modern-button guidelines-btn"
        >
          Open Competency Guidelines <span className="button-icon">ðŸ“Š</span>
        </button>
        {showPopup && <Guidelines onClose={() => setShowPopup(false)} />}
      </div>

      {/* Main Content */}
      <div className="content-container">
        <div className="content">
          <ScoreFormManager
            categories={categories}
            onScoresChange={setScores}
          />
          {/* Radar Chart */}
          <div className="chart-wrapper">
            <SpiderChart
              currentScores={scores.currentScores}
              projectedScores={scores.projectedScores}
              categories={categories}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
