import React from "react";
import SpiderChart from "./SpiderChart";
import { saveAs } from "file-saver";
import { jsPDF } from "jspdf";
import "jspdf-autotable";

const SupervisorSpiderChart = ({
  categories,
  currentScores,
  projectedScores,
  supervisorScores,
  status,
  approvalComments,
  userName,
}) => {
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text(`Competency Assessment Report for ${userName}`, 20, 20);
    doc.setFontSize(12);
    doc.text(`Assessment Status: ${status}`, 20, 30);
    if (approvalComments) {
      doc.text("Supervisor Comments:", 20, 40);
      doc.text(approvalComments, 20, 50, { maxWidth: 170 });
    }

    const tableData = categories.map((cat, idx) => [
      cat,
      currentScores[cat] || 0,
      projectedScores[cat] || 0,
      supervisorScores[cat] || 0,
    ]);

    doc.autoTable({
      startY: approvalComments ? 60 : 40,
      head: [
        ["Category", "Current Score", "Projected Score", "Supervisor Score"],
      ],
      body: tableData,
      theme: "striped",
      headStyles: { fillColor: [156, 202, 13] },
    });

    doc.save(`${userName}_Competency_Report.pdf`);
  };

  return (
    <div className="supervisor-chart-container">
      <div className="review-header">
        <h3 className="review-title">Supervisor's Assessment</h3>
        <div className="status-indicator status-{status.toLowerCase()}">
          Status: {status}
        </div>
      </div>
      <div className="progress-bar">
        <div
          className={`progress-fill status-${status.toLowerCase()}`}
          style={{
            width:
              status === "Approved"
                ? "100%"
                : status === "Rejected"
                ? "50%"
                : "25%",
          }}
        ></div>
      </div>
      <div className="chart-transition">
        <SpiderChart
          currentScores={currentScores}
          projectedScores={projectedScores}
          supervisorScores={supervisorScores}
          categories={categories}
        />
      </div>
      {approvalComments && (
        <div className="comments-section">
          <p className="comments-label">Supervisor Comments:</p>
          <p className="comments-text">{approvalComments}</p>
        </div>
      )}
      <button className="modern-button download-btn" onClick={generatePDF}>
        Download Report <span className="button-icon">ðŸ“„</span>
      </button>
    </div>
  );
};

export default SupervisorSpiderChart;
