// src/components/ScoreFormManager.js
import React, { useState } from "react";
import axios from "axios";
import InputForm from "./ScoreInput"; // Adjust path as needed
import "./ScoreInput.css";

export default function ScoreFormManager({ categories, onScoresChange }) {
  const [currentScores, setCurrentScores] = useState(
    Object.fromEntries(categories.map((cat) => [cat, 0]))
  );
  const [projectedScores, setProjectedScores] = useState(
    Object.fromEntries(categories.map((cat) => [cat, 0]))
  );
  const [sending, setSending] = useState(false);
  const [message, setMessage] = useState("");

  // Update parent component with scores whenever they change
  React.useEffect(() => {
    onScoresChange({ currentScores, projectedScores });
  }, [currentScores, projectedScores, onScoresChange]);

  const handleScoreChange = (setter) => (e) => {
    const { name, value } = e.target;
    const val = Math.min(5, Math.max(0, parseInt(value) || 0));
    setter((prev) => ({ ...prev, [name]: val }));
  };

  const handleSendScores = async () => {
    setSending(true);
    setMessage("");

    const token = localStorage.getItem("token");
    console.log("Token being sent:", token);
    console.log("Current Scores:", currentScores);
    console.log("Projected Scores:", projectedScores);

    // âœ… Validate that all fields are filled (non-zero or required)
    const isValidScore = (val) =>
      typeof val === "number" && !isNaN(val) && val > 0 && val <= 5;

    const allCurrentFilled = Object.values(currentScores).every(isValidScore);
    const allProjectedFilled =
      Object.values(projectedScores).every(isValidScore);

    if (!allCurrentFilled || !allProjectedFilled) {
      setMessage(
        "âš ï¸ Please fill in **all** fields with a score between 1 and 5."
      );
      setSending(false);
      return;
    }
    try {
      console.log("Current Scores:", currentScores); // âœ… only logs when valid
      console.log("Projected Scores:", projectedScores);

      const response = await axios.post(
        "http://localhost:4000/api/scores",
        {
          current: currentScores,
          projected: projectedScores,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      ); // ... handle success
    } catch (error) {
      // ... handle error
    }

    if (!token) {
      setMessage("No token found. Please log in first.");
      setSending(false);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:4000/api/scores",
        {
          current: currentScores,
          projected: projectedScores,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 200 || response.status === 201) {
        setMessage("âœ… Scores submitted successfully!");
      } else {
        setMessage("âš ï¸ Something went wrong. Please try again.");
      }
    } catch (error) {
      console.error("Error submitting scores:", error.response?.data);
      const status = error.response?.status;

      if (status === 403) {
        localStorage.removeItem("token");
        window.location.href = "/login";
      }

      setMessage(
        status === 403
          ? "ðŸš« Session expired or invalid token. Please log in again."
          : status === 409
          ? "âŒ You have already submitted scores."
          : "âŒ Error submitting scores. Try again."
      );
    } finally {
      setSending(false);
    }
  };

  return (
    <>
      {" "}
      <InputForm
        title="CURRENT STATE (JUNE 2025)"
        scores={currentScores}
        onChange={handleScoreChange(setCurrentScores)}
        categories={categories}
      />
      <InputForm
        title="TARGET FOR 2027"
        scores={projectedScores}
        onChange={handleScoreChange(setProjectedScores)}
        categories={categories}
      />
      <div className="submit-section">
        <button
          className="modern-button send-btn"
          onClick={handleSendScores}
          disabled={sending}
        >
          {sending ? "Sending..." : "Send Scores"}
        </button>
        {message && <p className="status-message">{message}</p>}
      </div>
    </>
  );
}
