import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import InputForm from "../ScoreInput/ScoreInput";
import SpiderChart from "./SpiderChart";
import Guidelines from "../Guidelines/Guidelines";
import { Link } from "react-router-dom";
import LOGO from "../../assets/nock j.png";
import { getUserFromToken } from "../../utils/auth";
import "./Modern.css";

const categories = [
  "Geology",
  "Geophysics",
  "Geochemistry",
  "Petroleum Engineering",
  "Project Management",
  "Renewable Energy",
  "Field Work",
  "Reporting",
];

export default function ModernSpiderChart() {
  const [showPopup, setShowPopup] = useState(false);
  const [sending, setSending] = useState(false);
  const [message, setMessage] = useState("");
  const [currentScores, setCurrentScores] = useState(
    Object.fromEntries(categories.map((cat) => [cat, 0]))
  );
  const [projectedScores, setProjectedScores] = useState(
    Object.fromEntries(categories.map((cat) => [cat, 0]))
  );

  const handleScoreChange = (setter) => (e) => {
    const { name, value } = e.target;
    const val = Math.min(5, Math.max(0, parseInt(value) || 0));
    setter((prev) => ({ ...prev, [name]: val }));
  };

  const handleSendScores = async () => {
    setSending(true);
    setMessage("");

    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("No token found. Please log in first.");
      setSending(false);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:4000/api/scores",
        {
          current: currentScores,
          projected: projectedScores,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 200 || response.status === 201) {
        setMessage("âœ… Scores submitted successfully!");
      } else {
        setMessage("âš ï¸ Something went wrong. Please try again.");
      }
    } catch (error) {
      console.error("Error submitting scores:", error);
      setMessage(
        error.response?.status === 403
          ? "ðŸš« Forbidden: You are not authorized."
          : "âŒ Error submitting scores. Try again."
      );
    } finally {
      setSending(false);
    }
  };

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setIsDropdownOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const user = getUserFromToken();

  if (!user) {
    return "Please log in";
  }

  // Extract first letters of first and last names
  const nameParts = user.name.split(" ");
  const initials =
    nameParts.length >= 2
      ? `${nameParts[0][0]}${nameParts[nameParts.length - 1][0]}`
      : nameParts[0][0];

  return (
    <div className="modern-chart-container">
      {/* Logo and Header */}
      <header className="modern-header">
        <div className="modern-logo">
          <div className="logo-placeholder">
            <img className="logo-icon" src={LOGO} alt="logo" />
            <span className="logo-text">Energizing Kenya</span>
          </div>
        </div>
        <div className="modern-profile" ref={dropdownRef}>
          <div className="profile-container" onClick={toggleDropdown}>
            <div className="profile-avatar">{initials}</div>
            <div className="profile-info">
              <h3>{user.name}</h3>
              <p>{user.occupation}</p>
            </div>
            <div className={`dropdown-arrow ${isDropdownOpen ? "open" : ""}`}>
              â–¼
            </div>
          </div>

          <div className={`dropdown-menu ${isDropdownOpen ? "open" : ""}`}>
            <Link to="/" className="dropdown-item">
              View Assessement
            </Link>
            <div className="dropdown-divider"></div>
            <Link to="/logout" className="dropdown-item logout">
              Log Out
            </Link>
          </div>
        </div>
      </header>
      <div className="modern-title-container">
        <h2 className="modern-title">Upstream Competency Mapping</h2>
        <div className="title-decoration"></div>
      </div>
      {/* Guidelines Popup */}
      <div className="modern-guidelines-button">
        <button
          onClick={() => setShowPopup(true)}
          className="modern-button guidelines-btn"
        >
          Open Competency Guidelines <span className="button-icon">ðŸ“Š</span>
        </button>
        {showPopup && <Guidelines onClose={() => setShowPopup(false)} />}
      </div>

      {/* Main Content */}
      <div className="content-container">
        <div className="content">
          <InputForm
            title="CURRENT STATE (JUNE 2025)"
            scores={currentScores}
            onChange={handleScoreChange(setCurrentScores)}
            categories={categories}
          />
          <InputForm
            title="TARGET FOR 2027"
            scores={projectedScores}
            onChange={handleScoreChange(setProjectedScores)}
            categories={categories}
          />

          {/* Submit Button */}
          <div className="submit-section">
            <button
              className="modern-button send-btn"
              onClick={handleSendScores}
              disabled={sending}
            >
              {sending ? "Sending..." : "Send Scores"}
            </button>
            {message && <p className="status-message">{message}</p>}
          </div>

          {/* Radar Chart */}
          <div className="chart-wrapper">
            <SpiderChart
              currentScores={currentScores}
              projectedScores={projectedScores}
              categories={categories}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
