import React, { useState } from "react";
import { Radar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend
);

const categories = ["Math", "English", "Science", "Music", "Sports"];

export default function ModernSpiderChart() {
  const [scores, setScores] = useState({
    Math: 0,
    English: 0,
    Science: 0,
    Music: 0,
    Sports: 0,
  });

  const data = {
    labels: categories,
    datasets: [
      {
        label: "Student Score",
        data: categories.map((cat) => scores[cat]),
        backgroundColor: "rgba(54, 162, 235, 0.4)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 2,
        pointBackgroundColor: "rgba(54, 162, 235, 1)",
        pointRadius: 5,
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      r: {
        min: 0,
        max: 5,
        ticks: {
          stepSize: 1,
          color: "#555",
          backdropColor: "transparent",
        },
        pointLabels: {
          font: {
            size: 14,
          },
          color: "#333",
        },
        grid: {
          color: "#ccc",
        },
        angleLines: {
          color: "#999",
        },
      },
    },
    plugins: {
      legend: {
        display: true,
        labels: {
          color: "#333",
          font: {
            size: 14,
          },
        },
      },
    },
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const val = Math.min(5, Math.max(0, parseInt(value) || 0)); // Limit between 0–5
    setScores((prev) => ({ ...prev, [name]: val }));
  };

  return (
    <div
      style={{
        padding: "30px",
        maxWidth: "700px",
        margin: "auto",
        background: "#fff",
        boxShadow: "0 0 15px rgba(0,0,0,0.1)",
        borderRadius: "10px",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        Modern Spider Chart
      </h2>
      <form
        style={{
          display: "flex",
          justifyContent: "space-between",
          flexWrap: "wrap",
          gap: "10px",
        }}
      >
        {categories.map((cat) => (
          <div key={cat} style={{ flex: "1 0 45%" }}>
            <label style={{ display: "block", marginBottom: "5px" }}>
              {cat}
            </label>
            <input
              type="number"
              name={cat}
              value={scores[cat]}
              min="0"
              max="5"
              onChange={handleChange}
              style={{
                padding: "8px",
                width: "100%",
                borderRadius: "5px",
                border: "1px solid #ccc",
              }}
            />
          </div>
        ))}
      </form>

      <div style={{ marginTop: "40px" }}>
        <Radar data={data} options={options} />
      </div>
    </div>
  );
}
