import React, { useState, useRef, useEffect } from "react";
import LOGO from "../../assets/nock j.png";
import axios from "axios";
import { Link } from "react-router-dom";
import "./SupDash.css";

export default function SupDash() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    teamSize: 0,
    pendingReviews: 0,
    completedReviews: 0,
    totalAssessments: 0,
  });
  const [pendingReviews, setPendingReviews] = useState([]);
  const [completedReviews, setCompletedReviews] = useState([]);
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch user data
        const userRes = await axios.get("http://localhost:4000/api/auth/me", {
          withCredentials: true,
        });
        const userData = userRes.data.user;
        setUser(userData);

        if (userData.role !== "supervisor") {
          throw new Error("Access denied: Not a supervisor");
        }

        // Fetch dashboard stats
        const statsRes = await axios.get(
          "http://localhost:4000/api/scores/stats",
          {
            withCredentials: true,
          }
        );
        setStats(statsRes.data);

        // Fetch pending reviews
        const pendingRes = await axios.get(
          "http://localhost:4000/api/scores/pending",
          {
            withCredentials: true,
          }
        );
        setPendingReviews(pendingRes.data);

        // Fetch completed reviews
        const completedRes = await axios.get(
          "http://localhost:4000/api/scores/completed",
          {
            withCredentials: true,
          }
        );
        setCompletedReviews(completedRes.data);
      } catch (err) {
        console.error(
          "Error fetching data:",
          err.response?.data || err.message
        );
        setError(
          err.response?.data?.message || "Failed to load dashboard data"
        );
        if (err.response?.status === 401) {
          window.location.href = "/login";
        }
      }
    };
    fetchData();

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!user) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">
          <div className="spinner-sector spinner-sector-primary"></div>
          <div className="spinner-sector spinner-sector-secondary"></div>
          <div className="spinner-sector spinner-sector-tertiary"></div>
        </div>
        <div className="loading-content">
          <img src={LOGO} alt="NOCK Logo" className="loading-logo" />
          <h2 className="loading-text">Loading Dashboard Data</h2>
          <p className="loading-subtext">Please wait while we prepare</p>
        </div>
      </div>
    );
  }

  const nameParts = user.name.split(" ");
  const initials =
    nameParts.length >= 2
      ? `${nameParts[0][0]}${nameParts[nameParts.length - 1][0]}`
      : nameParts[0][0];

  return (
    <div className="dashboard-container">
      <header className="modern-header">
        <div className="modern-logo">
          <div className="logo-placeholder">
            <img className="logo-icon" src={LOGO} alt="logo" />
            <span className="logo-text">Energizing Kenya</span>
          </div>
        </div>
        <div className="modern-profile" ref={dropdownRef}>
          <div className="profile-container" onClick={toggleDropdown}>
            <div className="profile-avatar">{initials}</div>
            <div className="profile-info">
              <h3>{user.name}</h3>
              <p>{user.occupation}</p>
            </div>
            <div className={`dropdown-arrow ${isDropdownOpen ? "open" : ""}`}>
              ▼
            </div>
          </div>
          <div className={`dropdown-menu ${isDropdownOpen ? "open" : ""}`}>
            <div className="dropdown-divider"></div>
            <Link to="/logout" className="dropdown-item logout">
              Log Out
            </Link>
          </div>
        </div>
      </header>
      <div className="dashboard-header">
        <h1>Competency Assessment</h1>
        <h2>Supervisor Dashboard</h2>
        <div className="welcome-message">
          Welcome, <span className="user-name">{user.name}</span>
          <p>Manage and review competency assessments for your team members.</p>
        </div>
        <div className="department-info">
          Department: <span className="highlight">Upstream</span> • Team Size:{" "}
          <span className="highlight">{stats.teamSize} employee(s)</span>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="stats-container">
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon pending"></div>
              <h3>Pending Reviews</h3>
            </div>
            <div className="stat-value">{stats.pendingReviews}</div>
          </div>
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon completed"></div>
              <h3>Completed Reviews</h3>
            </div>
            <div className="stat-value">{stats.completedReviews}</div>
          </div>
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon total"></div>
              <h3>Total Team Assessments</h3>
            </div>
            <div className="stat-value">{stats.totalAssessments}</div>
          </div>
        </div>

        <div className="actions-section">
          <Link to="/employeeAssessments">
            <button className="action-button">View All Assessments</button>
          </Link>
        </div>

        <div className="reviews-section">
          <div className="review-card">
            <h3 className="review-title">Pending Reviews</h3>
            {pendingReviews.length === 0 ? (
              <div className="empty-state">No pending reviews</div>
            ) : (
              pendingReviews.map((review) => (
                <div key={review._id} className="review-item">
                  <p>
                    <strong>{review.userId.name}</strong> submitted on{" "}
                    {new Date(review.submittedAt).toLocaleDateString()}
                  </p>
                  <Link to={`/review/${review._id}`}>
                    <button className="review-action">Review Now</button>
                  </Link>
                </div>
              ))
            )}
          </div>
          <div className="review-card">
            <h3 className="review-title">Recently Completed Reviews</h3>
            {completedReviews.length === 0 ? (
              <div className="empty-state">No completed reviews</div>
            ) : (
              completedReviews.map((review) => (
                <div key={review._id} className="review-item">
                  <p>
                    <strong>{review.userId.name}</strong> - {review.status} on{" "}
                    {new Date(review.reviewedAt).toLocaleDateString()}
                  </p>
                  <p>Feedback: {review.reviewFeedback || "None"}</p>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
