// src/components/ScoreFormManager.js
import React, { useState } from "react";
import axios from "axios";
import InputForm from "./ScoreInput"; // Adjust path as needed
import "./ScoreInput.css";

export default function ScoreFormManager({ categories, onScoresChange }) {
  const [currentScores, setCurrentScores] = useState(
    Object.fromEntries(categories.map((cat) => [cat, 0]))
  );
  const [projectedScores, setProjectedScores] = useState(
    Object.fromEntries(categories.map((cat) => [cat, 0]))
  );
  const [sending, setSending] = useState(false);
  const [message, setMessage] = useState("");

  // Update parent component with scores whenever they change
  React.useEffect(() => {
    onScoresChange({ currentScores, projectedScores });
  }, [currentScores, projectedScores, onScoresChange]);

  const handleScoreChange = (setter) => (e) => {
    const { name, value } = e.target;
    const val = Math.min(5, Math.max(0, parseInt(value) || 0));
    setter((prev) => ({ ...prev, [name]: val }));
  };

  const handleSendScores = async () => {
    setSending(true);
    setMessage("");

    const token = localStorage.getItem("token");
    console.log("Token being sent:", token); // Debug token

    if (!token) {
      setMessage("No token found. Please log in first.");
      setSending(false);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:4000/api/scores",
        {
          current: currentScores,
          projected: projectedScores,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 200 || response.status === 201) {
        setMessage("‚úÖ Scores submitted successfully!");
      } else {
        setMessage("‚ö†Ô∏è Something went wrong. Please try again.");
      }
    } catch (error) {
      console.error("Error submitting scores:", error.response?.data);
      setMessage(
        error.response?.status === 403
          ? "üö´ Session expired or invalid token. Please log in again."
          : "‚ùå Error submitting scores. Try again."
      );
      if (error.response?.status === 403) {
        localStorage.removeItem("token"); // Clear invalid/expired token
        window.location.href = "/login"; // Redirect to login
      }
    } finally {
      setSending(false);
    }
  };

  return (
    <>
      <div className="content-container">
        <div>
          {" "}
          <div
            className="content
          "
          >
            <InputForm
              title="CURRENT STATE (JUNE 2025)"
              scores={currentScores}
              onChange={handleScoreChange(setCurrentScores)}
              categories={categories}
            />
            <InputForm
              title="TARGET FOR 2027"
              scores={projectedScores}
              onChange={handleScoreChange(setProjectedScores)}
              categories={categories}
            />
            <div className="submit-section">
              <button
                className="modern-button send-btn"
                onClick={handleSendScores}
                disabled={sending}
              >
                {sending ? "Sending..." : "Send Scores"}
              </button>
              {message && <p className="status-message">{message}</p>}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
