import React, { useState } from "react";
import axios from "axios";
import InputForm from "./ScoreInput";
import "./ScoreInput.css";

const ScoreFormManager = ({ categories, onScoresChange, initialScores }) => {
  const [currentScores, setCurrentScores] = useState(
    initialScores.currentScores
  );
  const [projectedScores, setProjectedScores] = useState(
    initialScores.projectedScores
  );
  const [submissionStatus, setSubmissionStatus] = useState(null);
  const [error, setError] = useState(null);

  const handleCurrentChange = (category, value) => {
    const newScores = { ...currentScores, [category]: parseInt(value) || 0 };
    setCurrentScores(newScores);
    onScoresChange({ currentScores: newScores, projectedScores });
  };

  const handleProjectedChange = (category, value) => {
    const newScores = { ...projectedScores, [category]: parseInt(value) || 0 };
    setProjectedScores(newScores);
    onScoresChange({ currentScores, projectedScores: newScores });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSubmissionStatus(null);

    try {
      await axios.post(
        "http://localhost:4000/api/scores",
        {
          current: currentScores,
          projected: projectedScores,
        },
        { withCredentials: true }
      );
      setSubmissionStatus("Scores submitted for approval.");
      setTimeout(() => setSubmissionStatus(null), 3000); // Clear message after 3s
    } catch (err) {
      setError(err.response?.data?.error || "Failed to submit scores.");
    }
  };

  return (
    <div className="score-form-container">
      <h3>Submit Competency Scores</h3>
      <form onSubmit={handleSubmit}>
        <div className="score-inputs">
          {categories.map((category) => (
            <div key={category} className="input-group">
              <label>{category}</label>
              <div>
                <input
                  type="number"
                  min="0"
                  max="5"
                  value={currentScores[category] || 0}
                  onChange={(e) =>
                    handleCurrentChange(category, e.target.value)
                  }
                  placeholder="Current Score"
                />
                <input
                  type="number"
                  min="0"
                  max="5"
                  value={projectedScores[category] || 0}
                  onChange={(e) =>
                    handleProjectedChange(category, e.target.value)
                  }
                  placeholder="Projected Score"
                />
              </div>
            </div>
          ))}
        </div>
        {error && <p className="error-message">{error}</p>}
        {submissionStatus && (
          <p className="submission-message">{submissionStatus}</p>
        )}
        <button type="submit" className="submit-button">
          Submit Scores
        </button>
      </form>
    </div>
  );
};

export default ScoreFormManager;
