import React, { useState } from "react";
import { Radar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
} from "chart.js";
import "./Modern.css";

ChartJS.register(
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend
);

const categories = ["Math", "English", "Science", "Music", "Sports"];

export default function ModernSpiderChart() {
  const [scores, setScores] = useState({
    Math: 0,
    English: 0,
    Science: 0,
    Music: 0,
    Sports: 0,
  });

  const data = {
    labels: categories,
    datasets: [
      {
        label: "Student Score",
        data: categories.map((cat) => scores[cat]),
        backgroundColor: "rgba(54, 162, 235, 0.4)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 2,
        pointBackgroundColor: "rgba(54, 162, 235, 1)",
        pointRadius: 4,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      r: {
        min: 0,
        max: 5,
        ticks: {
          stepSize: 1,
          color: "#555",
          backdropColor: "transparent",
        },
        pointLabels: {
          font: {
            size: 14,
          },
          color: "#333",
        },
        grid: {
          color: "#ddd",
        },
        angleLines: {
          color: "#999",
        },
      },
    },
    plugins: {
      legend: {
        display: false,
      },
    },
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const val = Math.min(5, Math.max(0, parseInt(value) || 0));
    setScores((prev) => ({ ...prev, [name]: val }));
  };

  return (
    <div className="chart-container">
      <h2>Spider Chart</h2>
      <div className="content">
        <form className="inputs">
          {categories.map((cat) => (
            <div key={cat} className="input-group">
              <label>{cat}</label>
              <input
                type="number"
                name={cat}
                value={scores[cat]}
                min="0"
                max="5"
                onChange={handleChange}
              />
            </div>
          ))}
        </form>
        <div className="chart">
          <Radar data={data} options={options} />
        </div>
      </div>
    </div>
  );
}
