import React, { useState, useRef, useEffect } from "react";
import LOGO from "../../assets/nock j.png";
import { Link } from "react-router-dom";
import axios from "axios";
import "./EmployeeAssessments.css";

function DashboardIcon() {
  return (
    <svg
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <rect x="3" y="3" width="8" height="8" rx="1" fill="#0c0c0c" />
      <rect
        x="3"
        y="13"
        width="8"
        height="8"
        rx="1"
        fill="#0c0c0c"
        opacity="0.8"
      />
      <rect x="13" y="3" width="8" height="8" rx="1" fill="#0c0c0c" />
      <rect
        x="13"
        y="13"
        width="8"
        height="8"
        rx="1"
        fill="#0c0c0c"
        opacity="0.8"
      />
      <path d="M12 3V21" stroke="#e0e0e0" strokeWidth="1.5" />
      <path d="M3 12H21" stroke="#e0e0e0" strokeWidth="1.5" />
    </svg>
  );
}

export default function EmployeeAssessments() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [submissions, setSubmissions] = useState([]);
  const [filter, setFilter] = useState("All");
  const dropdownRef = useRef(null);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userRes = await axios.get("http://localhost:4000/api/auth/me", {
          withCredentials: true,
        });
        const userData = userRes.data.user;
        console.log("Fetched user data:", userData);
        setUser(userData);

        if (userData.role === "supervisor") {
          const submissionsRes = await axios.get(
            "http://localhost:4000/api/scores/team",
            {
              withCredentials: true,
            }
          );
          setSubmissions(submissionsRes.data.submissions);
        }
      } catch (err) {
        console.error(
          "Error fetching user:",
          err.response?.data || err.message
        );
        setError(err.response?.data?.message || "Failed to load user data");
        if (err.response?.status === 401) {
          window.location.href = "/login";
        }
      }
    };
    fetchUserData();

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const handleApproveReject = async (submissionId, status, comments = "") => {
    try {
      await axios.post(
        `http://localhost:4000/api/scores/${submissionId}/approve`,
        { status, approvalComments: comments },
        { withCredentials: true }
      );
      setSubmissions((prev) =>
        prev.map((sub) =>
          sub._id === submissionId
            ? {
                ...sub,
                status,
                approvedAt: new Date(),
                approvalComments: comments,
              }
            : sub
        )
      );
    } catch (err) {
      console.error("Approval error:", err);
      setError(err.response?.data?.error || "Failed to process approval.");
    }
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  const filteredSubmissions =
    filter === "All"
      ? submissions
      : submissions.filter((sub) => sub.status === filter.toLowerCase());

  if (!user) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">
          <div className="spinner-sector spinner-sector-primary"></div>
          <div className="spinner-sector spinner-sector-secondary"></div>
          <div className="spinner-sector spinner-sector-tertiary"></div>
        </div>
        <div className="loading-content">
          <img src={LOGO} alt="NOCK Logo" className="loading-logo" />
          <h2 className="loading-text">Loading Assessment Data</h2>
          <p className="loading-subtext">Please wait while we prepare</p>
        </div>
      </div>
    );
  }

  const nameParts = user.name.split(" ");
  const initials =
    nameParts.length >= 2
      ? `${nameParts[0][0]}${nameParts[nameParts.length - 1][0]}`
      : nameParts[0][0];

  return (
    <div className="assessments-container">
      <header className="modern-header">
        <div className="modern-logo">
          <div className="logo-placeholder">
            <img className="logo-icon" src={LOGO} alt="logo" />
            <span className="logo-text">Energizing Kenya</span>
          </div>
        </div>
        <Link className="dashlink" to="/supDash">
          <DashboardIcon />
          Dashboard
        </Link>
        <div className="modern-profile" ref={dropdownRef}>
          <div className="profile-container" onClick={toggleDropdown}>
            <div className="profile-avatar">{initials}</div>
            <div className="profile-info">
              <h3>{user.name}</h3>
              <p>{user.occupation}</p>
            </div>
            <div className={`dropdown-arrow ${isDropdownOpen ? "open" : ""}`}>
              ▼
            </div>
          </div>
          <div className={`dropdown-menu ${isDropdownOpen ? "open" : ""}`}>
            <div className="dropdown-divider"></div>
            <Link to="/logout" className="dropdown-item logout">
              Log Out
            </Link>
          </div>
        </div>
      </header>
      <h2>Employee Assessments</h2>
      <div className="assessments-header">
        <div className="status-message">
          {error && <p className="error-message">{error}</p>}
          {submissions.length === 0 ? (
            <p>None of your team members have submitted assessments.</p>
          ) : (
            <p>{filteredSubmissions.length} assessment(s) found.</p>
          )}
        </div>
      </div>
      <div className="assessments-content">
        <div className="action-card">
          <button className="view-all-btn">
            View Team Performance
            <span className="arrow-icon">→</span>
          </button>
        </div>
        <div className="filter-tabs">
          <div
            className={`tab ${filter === "All" ? "active" : ""}`}
            onClick={() => handleFilterChange("All")}
          >
            All
          </div>
          <div
            className={`tab ${filter === "Pending" ? "active" : ""}`}
            onClick={() => handleFilterChange("Pending")}
          >
            Pending
          </div>
          <div
            className={`tab ${filter === "Completed" ? "active" : ""}`}
            onClick={() => handleFilterChange("Completed")}
          >
            Completed
          </div>
        </div>
        {filteredSubmissions.length === 0 ? (
          <div className="empty-state-card">
            <div className="empty-state-content">
              <div className="empty-icon">🔍</div>
              <h3>No Assessments</h3>
              <p>Try refreshing or check back later</p>
            </div>
          </div>
        ) : (
          <div className="submissions-list">
            {filteredSubmissions.map((submission) => (
              <div key={submission._id} className="submission-card">
                <h4>{submission.submitter.name}</h4>
                <p>Status: {submission.status}</p>
                <p>
                  Submitted:{" "}
                  {new Date(submission.submittedAt).toLocaleDateString()}
                </p>
                {submission.approvalComments && (
                  <p>Comments: {submission.approvalComments}</p>
                )}
                {submission.status === "pending" && (
                  <div className="submission-actions">
                    <button
                      className="approve-btn"
                      onClick={() =>
                        handleApproveReject(submission._id, "approved")
                      }
                    >
                      Approve
                    </button>
                    <button
                      className="reject-btn"
                      onClick={() =>
                        handleApproveReject(
                          submission._id,
                          "rejected",
                          prompt("Enter rejection comments (optional):")
                        )
                      }
                    >
                      Reject
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
