import React, { useState } from "react";
import axios from "axios"; // Make sure this is installed
import InputForm from "../ScoreInput/ScoreInput";
import SpiderChart from "./SpiderChart";
import Guidelines from "../Guidelines/Guidelines";
import LOGO from "../../assets/nock j.png";
import "./Modern.css";

const categories = [
  "Geology",
  "Geophysics",
  "Geochemistry",
  "Petroleum Engineering",
  "Project Management",
  "Renewable Energy",
  "Field Work",
  "Reporting",
];

export default function ModernSpiderChart() {
  const [showPopup, setShowPopup] = useState(false);
  const [sending, setSending] = useState(false);
  const [message, setMessage] = useState("");
  const [currentScores, setCurrentScores] = useState({
    Geology: 0,
    Geophysics: 0,
    Geochemistry: 0,
    "Petroleum Engineering": 0,
    "Project Management": 0,
    "Renewable Energy": 0,
    "Field Work": 0,
    Reporting: 0,
  });

  const [projectedScores, setProjectedScores] = useState({
    Geology: 0,
    Geophysics: 0,
    Geochemistry: 0,
    "Petroleum Engineering": 0,
    "Project Management": 0,
    "Renewable Energy": 0,
    "Field Work": 0,
    Reporting: 0,
  });

  const handleCurrentChange = (e) => {
    const { name, value } = e.target;
    const val = Math.min(5, Math.max(0, parseInt(value) || 0));
    setCurrentScores((prev) => ({ ...prev, [name]: val }));
  };

  const handleProjectedChange = (e) => {
    const { name, value } = e.target;
    const val = Math.min(5, Math.max(0, parseInt(value) || 0));
    setProjectedScores((prev) => ({ ...prev, [name]: val }));
  };

  const handleSendScores = async () => {
    setSending(true);
    setMessage("");
    try {
      const token = localStorage.getItem("token"); // Assumes login sets token
      const res = await axios.post(
        "http://localhost:4000/api/scores",
        {
          current: currentScores,
          projected: projectedScores,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage("Scores submitted successfully!");
    } catch (err) {
      setMessage("Error submitting scores. Try again.");
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="modern-chart-container">
      {/* header code... */}
      {/* title code... */}
      <div className="modern-guidelines-button">
        <button
          onClick={() => setShowPopup(true)}
          className="modern-button guidelines-btn"
        >
          Open Competency Guidelines
          <span className="button-icon">📊</span>
        </button>
        {showPopup && <Guidelines onClose={() => setShowPopup(false)} />}
      </div>

      <div className="content-container">
        <div className="content">
          <InputForm
            title="CURRENT STATE (JUNE 2025)"
            scores={currentScores}
            onChange={handleCurrentChange}
            categories={categories}
          />
          <InputForm
            title="TARGET FOR 2027"
            scores={projectedScores}
            onChange={handleProjectedChange}
            categories={categories}
          />
          <div className="submit-section">
            <button
              className="modern-button send-btn"
              onClick={handleSendScores}
              disabled={sending}
            >
              {sending ? "Sending..." : "Send Scores"}
            </button>
            {message && <p className="status-message">{message}</p>}
          </div>
          <div className="chart-wrapper">
            <SpiderChart
              currentScores={currentScores}
              projectedScores={projectedScores}
              categories={categories}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
